import numpy as np
import matplotlib.pyplot as plt

dr = "/Volumes/lowegrp/Data/Kristina/MDCK_WT_Pure/Sine_Wave/Grid_Search/2_gen/"

for line in open(dr + "MSE_Summary.txt", "r"):
    line = line.rstrip()
    if line.startswith("Amp"):
        title = line
    else:
        line += " "
        mse_data = line.split(" ")
        mse_data = [float(value) for value in mse_data[:-1]]
        if len(mse_data) == 361:
            x = 6.0, 42.0, 361

        elif len(mse_data) == 101:
            x = 2.0, 12.0, 101
        elif len(mse_data) == 121:
            x = 12.0, 24.0, 121
        parameter = np.linspace(x[0], x[1], x[2])
        plt.plot(parameter, mse_data, color="orange")
        plt.title(title)
        plt.xlabel("Iterated Parameter")
        plt.ylabel("Sum of 2-gen families MSEs")
        plt.savefig(dr + "Period_MSE.png", bbox_inches="tight")
        plt.show()
        plt.close()

